import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { toast } from 'react-hot-toast';
import { add,remove } from '../redux/slices/CartSlice';

const Product = ({post}) => {
    const {cart}=useSelector((state)=>state);
  const dispatch=useDispatch();
  const addToCart=()=>{
      dispatch(add(post));
      toast.success("Item added to Cart");
  }

  const removeFromCart=()=>{
    dispatch(remove(post.id));
    toast.success("Item removed from Cart");
}


  return (
    <div className='flex flex-col rounded-md items-center justify-between hover:scale-110 transition-all duration-300 ease-in hover:shadow-[rgba(0,_0,_0,_0.4)_0px_30px_90px] '>
         <div>
            <p className='text-'>{post.title.split(" ").slice(0,2).join(" ")+"..."}</p>
         </div>
         <div>
            <p>{post.description.split(" ").slice(0,10).join(" ")+" ..."}</p>
         </div>
         <div >
            <img src={post.image} width={150} alt="" />
         </div>
         <div>
            <p>{post.price}</p>
         </div>
      
            {
                cart.some((p)=>p.id===post.id)? 
                (
                <button onClick={removeFromCart}>Remove Item</button>
                )
                :
                (
                    <button onClick={addToCart} >Add to Cart</button>
                 )
            }
        
         

    </div>
  )
}

export default Product
