import React, { useEffect, useState } from 'react'
import Spinner from '../components/Spinner';
import ItemList from '../components/ItemList';


const Home = () => {
    const API_URL="https://jsonplaceholder.typicode.com/photos";
  const [loading,setLoading]=useState(false);
  const [posts,setPosts]=useState([]);

    async function fetchData(){
        setLoading(true);
        try {
         const result=await fetch(API_URL);
        const data=await result.json();
        console.log(data);
         setPosts(data.slice(0,20));
          // console.log(posts);

        } 
        catch (error) {
            console.log(error.message);
            setPosts([]);
        }
        
        setLoading(false);
    }

    useEffect(()=>{
        fetchData();
        // console.log();
    },[])


  return (
    <div >
        
           <form >
        <input
          type="text"
          placeholder="New Item ID"
          // value={newItemId}
          // onChange={(e) => setNewItemId(e.target.value)}
        />
        <input
          type="text"
          placeholder="New Item Title"
          // value={newItemTitle}
          // onChange={(e) => setNewItemTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="New Item Image URL" // Adjust placeholder and label based on your needs
          // value={newItemImage}
          // onChange={(e) => setNewItemImage(e.target.value)}
        />
        <button type="submit">Add New Item</button>
      </form>

           {
             loading?(<Spinner/>):(
                posts.length>0 ?
                (
                    <div className='grid xsm:grid-cols-1 sm:grid-cols-3 md:grid-cols-4 max-w-6xl p-2 mx-auto space-y-10 space-x-5 min-h-[80vh]'> 
                    
                    {posts.map((post)=>(
                        //  console.log("hii");
                      <ItemList key={post.id} post={post} />
                        
                    ))
                    }
                  
                  </div>

                )
                :
                <div>
                  <p>No data found!</p>
                </div>

            )
           }
    </div>
  )
}

export default Home
