import React from 'react'
import {FcDeleteDatabase} from "react-icons/fc"
import { remove } from '../redux/slices/CartSlice';
import { useDispatch } from 'react-redux';
import { toast } from 'react-hot-toast';

const CartItem = ({item}) => {

    const dispatch=useDispatch();
    const removeFromCart=()=>{
        dispatch(remove(item.id));
        toast.success("Item removed from Cart");
    }

  return (
    <div className=' flex flex-row flex-wrap gap-2  justify-around items-center  p-4 border-b-2 border-slate-700 mt-4 '>
        <div className='h-[180px]'>
            <img src={item.image} alt="" className=' h-full'/>
        </div>
        <div className='flex flex-col gap-2'>
            <h1 className='w-80 text-lg text-slate-700 font-semibold flex flex-wrap'>{item.title}</h1>
            <p className='w-80 text-[12px] text-gray-400'>{item.description.split(" ").slice(0,15).join(" ")+"..."}</p>
            <div className='flex justify-between'>
                <p className='text-green-600 font-semibold '>${item.price}</p>
                <div onClick={removeFromCart} className='bg-red-500 rounded-full p-2 '>
                <FcDeleteDatabase/>
                </div>
                
            </div>
        </div>
    </div>
  )
}

export default CartItem
