import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom';
import CartItem from '../components/CartItem';

const Cart = () => {

    const { cart } = useSelector((state) => state);
    const [totalAmount,setTotalAmount]=useState(0);

    useEffect(()=>{
        setTotalAmount(cart.reduce((acc,curr)=>acc+curr.price,0));
    },[cart])

    return (
        <div>
            {
                cart.length > 0 ?
                    (
                        <div className='flex flex-row  justify-center mx-auto gap-8 flex-wrap   '>
                             <div className=' '>
                                {
                                    cart.map((item,index)=>{
                                        return <CartItem key={item.id} item={item} itemIndex={index}/>
                                    })
                                }
                             </div>
                             <div className='flex flex-col justify-between mt-10'>
                                  <div>
                                    <h1 className='text-green-700 font-bold uppercase text-[14px]'>Your Cart</h1>
                                    <h1 className='text-3xl font-semibold text-green-600 uppercase'>Summary</h1>
                                    <p className='text-[14px] pt-2'>
                                        <span >Total Items:{cart.length}</span>
                                    </p>
                                  </div>
                                  <div>
                                    <p className='text-[14px] '>Total Amount: <span className='font-bold'>${totalAmount}</span></p>
                                    <button className='bg-green-700 text-white py-2 mt-2 w-full rounded-md font-semibold'>Checkout Now</button>
                                  </div>

                             </div>
                        </div>
                    ) :

                    (
                        <div className='absolute top-[50%] left-[44%] flex flex-col justify-center items-center gap-3 mx-auto'>
                            <h1 className='font-semibold '>Cart Empty</h1>
                            <Link to="/"><button className='bg-green-600 py-2 px-4  text-white rounded-md'>Shop Now</button></Link>
                        </div>
                    )



            }


        </div>
    )
}

export default Cart
